import React, { useState, useEffect } from "react";
import "../styles/PokemonList.css";

function PokemonList() {
    // Variable to store list using the Hook 'useState'
    // const [howItIs variable, update Function] = useState(original value)
    const [pokemons, setPokemons] = useState([]);
    // Variable to indicate that the data is processing
    // (starts empty)
    // using 'useState' again
    const [loading, setLoading] = useState(true);

    // useEffect: 
    // () => what to do 
    // [] when to do
    useEffect(() => {
        // Function to fetch the API
        async function fetchPokemons() {
            try {
                // Fetching 1302 Pokémon
                const response = await fetch('https://pokeapi.co/api/v2/pokemon/?limit=1302');
                // Converting to data to a JSON response
                const data = await response.json();
                // Updating the state with Pokémon

                // Fetching detailed data for each Pokémon
                const detailedPokemons = await Promise.all(
                    data.results.map(async (pokemon) => {
                        const detailsResponse = await fetch(pokemon.url);
                        const details = await detailsResponse.json();
                        return {
                            name: pokemon.name,
                            id: details.id,
                            types: details.types.map((typeInfo) => typeInfo.type.name), // Extracting types
                            stats: details.stats.map((stat) => ({
                                name: stat.stat.name, // Stat name (e.g., HP, Attack)
                                value: stat.base_stat // Stat value
                            })), 
                            image: details.sprites.front_default, // Pokémon image
                        };
                    })
                );

                setPokemons(detailedPokemons); // Storing the detailed Pokémon list
            } catch (error) {
                console.error('Couldn´t fetch Pokélist. Error ', error);
            } finally {
                // Giving an end to the loading
                setLoading(false);
            }
        }
        // Calling the function
        fetchPokemons();
        // Telling the fetch to be executed only once
    }, // That´s what to do
    []); // That´s when to do

    // Nice text while loading
    if (loading) {
        return <p>Loading Pokémon...</p>;
    }

    // Making the list with names of the Pokémon
    return (
        // Building a container
        <div className="pokemon-grid">
            {/* Using function map to build the cards */}
            {/* The Pokémon array that stores the state */}
            {pokemons.map((pokemon) => (
                <div className="pokemon-card" key={pokemon.id}>
                    <img
                        className="pokemon-image"
                        src={pokemon.image}
                        alt={pokemon.name}
                    />
                    <h3 className="pokemon-name">{pokemon.name}</h3>
                    <p className="pokemon-id">ID: #{pokemon.id}</p>
                    <p className="pokemon-types">
                        Types: {pokemon.types.join(", ")}
                    </p>
                    <div className="pokemon-stats">
                        <h4>Stats:</h4>
                        <ul>
                            {pokemon.stats.map((stat, index) => (
                                <li key={index}>
                                    {stat.name}: {stat.value}
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
            ))}
        </div>
    );
}

export default PokemonList;
